mapper.select:
   		select boss_nickname, worker_nickname,sign_board_name
		from organization
		where sign_board_name=#{signBoardName} and boss_nickname=#{bossMemberVO.nickName}

SELECT nickName 
FROM member
WHERE e_mail=#{value}

   		SELECT im.nickname as nickName, m.e_mail as eMail, im.invitation_date as invitationDate
		FROM member m, invitation_management im
		WHERE m.nickname=im.nickname and boss_nickname=#{bossMemberVO.nickName} and sign_board_name=#{signBoardName}
